import React from 'react';
import { Calendar, Phone, Mail, DollarSign, AlertCircle } from 'lucide-react';
import { STATUS_CONFIG, getClientStatus, getDealStatus } from '../utils/statusUtils';

const ClientCard = ({ client, onEdit }) => {
  const status = getClientStatus(client);
  const statusConfig = STATUS_CONFIG[status];
  const totalPipeline = client.deals?.reduce((sum, deal) => 
    sum + (deal.status !== 'closed_lost' ? deal.value : 0), 0) || 0;

  return (
    <div className={`p-4 rounded-lg shadow-md border-l-4 ${statusConfig.classes}`}>
      <div className="flex justify-between items-start mb-3">
        <div>
          <h3 className="font-semibold text-lg">{client.name}</h3>
          <p className="text-sm text-gray-600">{client.company}</p>
        </div>
        <button 
          onClick={() => onEdit(client)} 
          className="text-blue-600 hover:text-blue-700 text-sm"
        >
          Edit
        </button>
      </div>

      <div className="space-y-2 mb-4">
        {client.email && (
          <div className="flex items-center text-sm text-gray-600">
            <Mail size={16} className="mr-2" />
            <span>{client.email}</span>
          </div>
        )}
        {client.phone && (
          <div className="flex items-center text-sm text-gray-600">
            <Phone size={16} className="mr-2" />
            <span>{client.phone}</span>
          </div>
        )}
        <div className="flex items-center text-sm text-gray-600">
          <Calendar size={16} className="mr-2" />
          <span>Last Contact: {client.lastContact ? new Date(client.lastContact).toLocaleDateString() : 'Not contacted'}</span>
        </div>
        {totalPipeline > 0 && (
          <div className="flex items-center text-sm text-green-600 font-medium">
            <DollarSign size={16} className="mr-2" />
            <span>Pipeline: ${totalPipeline.toLocaleString()}</span>
          </div>
        )}
      </div>

      {client.deals && client.deals.length > 0 && (
        <div className="border-t pt-3">
          <h4 className="text-sm font-medium text-gray-700 mb-2">Active Deals</h4>
          <div className="space-y-2">
            {client.deals.map((deal, index) => {
              const dealStatus = getDealStatus(deal);
              return (
                <div key={index} className="flex items-center justify-between text-sm">
                  <span>{deal.title}</span>
                  <span className={`${dealStatus.color} font-medium`}>
                    ${deal.value.toLocaleString()}
                  </span>
                </div>
              );
            })}
          </div>
        </div>
      )}

      {client.followUpDate && new Date(client.followUpDate) > new Date() && (
        <div className="mt-3 flex items-center text-sm text-blue-600">
          <AlertCircle size={16} className="mr-2" />
          <span>Follow up on {new Date(client.followUpDate).toLocaleDateString()}</span>
        </div>
      )}

      {client.notes && (
        <div className="mt-3 text-sm text-gray-600 border-t pt-3">
          {client.notes}
        </div>
      )}
    </div>
  );
};

export default ClientCard;
