import React, { useState, useEffect } from 'react';
import { Search, UserPlus, Users, Bookmark } from 'lucide-react';
import ClientCard from './components/ClientCard';
import NewClientModal from './components/NewClientModal';
import Alert from './components/Alert';
import { getClientStatus, calculateMetrics } from './utils/statusUtils';
import { STATUS_CONFIG } from './utils/statusUtils';

const SalesSynth = () => {
  const [clients, setClients] = useState(() => {
    const savedClients = localStorage.getItem('clients');
    return savedClients ? JSON.parse(savedClients) : [];
  });
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedStatus, setSelectedStatus] = useState('all');
  const [showNewClientModal, setShowNewClientModal] = useState(false);
  const [selectedClient, setSelectedClient] = useState(null);
  const [showBookmarked, setShowBookmarked] = useState(false);
  const [alert, setAlert] = useState(null);

  useEffect(() => {
    localStorage.setItem('clients', JSON.stringify(clients));
  }, [clients]);

  const showAlert = (type, message) => {
    setAlert({ type, message });
    setTimeout(() => setAlert(null), 3000);
  };

  const handleEditClient = (client) => {
    setSelectedClient(client);
    setShowNewClientModal(true);
  };

  const handleSaveClient = (clientData) => {
    if (selectedClient) {
      setClients(prev => prev.map(c => 
        c.id === selectedClient.id ? { ...clientData, id: selectedClient.id } : c
      ));
      showAlert('success', 'Client updated successfully');
    } else {
      setClients(prev => [...prev, { ...clientData, id: Date.now().toString() }]);
      showAlert('success', 'New client added successfully');
    }
    setSelectedClient(null);
    setShowNewClientModal(false);
  };

  const handleToggleBookmark = (clientId) => {
    setClients(prev => {
      const updatedClients = prev.map(client => {
        if (client.id === clientId) {
          const newBookmarkState = !client.isBookmarked;
          showAlert(
            'info', 
            newBookmarkState 
              ? `${client.name} added to bookmarks`
              : `${client.name} removed from bookmarks`
          );
          return { ...client, isBookmarked: newBookmarkState };
        }
        return client;
      });
      return updatedClients;
    });
  };

  const metrics = calculateMetrics(clients);
  const bookmarkCount = clients.filter(c => c.isBookmarked).length;

  const filteredClients = clients.filter(client => {
    const matchesSearch = client.name?.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesStatus = selectedStatus === 'all' || getClientStatus(client) === selectedStatus;
    const matchesBookmark = !showBookmarked || client.isBookmarked;
    return matchesSearch && matchesStatus && matchesBookmark;
  });

  return (
    <div className="min-h-screen bg-gray-50">
      {alert && (
        <Alert
          type={alert.type}
          message={alert.message}
          onClose={() => setAlert(null)}
        />
      )}

      <header className="bg-white shadow">
        <div className="max-w-7xl mx-auto px-4 py-6">
          <div className="flex justify-between items-center">
            <h1 className="text-3xl font-bold text-gray-900">SalesSynth</h1>
            <div className="flex items-center space-x-6">
              <div className="text-sm">
                <div className="text-gray-500">Total Pipeline</div>
                <div className="text-xl font-bold">${metrics.pipeline.toLocaleString()}</div>
              </div>
              <div className="text-sm">
                <div className="text-gray-500">Active Clients</div>
                <div className="text-xl font-bold">{metrics.activeClients}</div>
              </div>
              <div className="text-sm">
                <div className="text-gray-500">Bookmarked</div>
                <div className="text-xl font-bold">{bookmarkCount}</div>
              </div>
            </div>
          </div>
        </div>
      </header>

      <div className="max-w-7xl mx-auto px-4 py-8">
        <div className="flex items-center justify-between mb-8">
          <div className="relative flex-1 max-w-xl">
            <Search className="absolute left-3 top-3 text-gray-400" size={20} />
            <input
              type="text"
              placeholder="Search clients..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              className="w-full pl-10 pr-4 py-2 border rounded-lg"
            />
          </div>
          <div className="flex items-center space-x-4">
            <button
              onClick={() => setShowBookmarked(!showBookmarked)}
              className={`px-4 py-2 rounded-lg flex items-center transition-all duration-200 ${
                showBookmarked 
                  ? 'bg-blue-100 text-blue-700 border border-blue-200' 
                  : 'bg-white text-gray-700 border hover:bg-gray-50'
              }`}
            >
              <Bookmark 
                size={20} 
                className={`mr-2 ${showBookmarked ? 'text-blue-600' : 'text-gray-500'}`} 
              />
              {showBookmarked ? 'Show All' : 'Show Bookmarked'}
            </button>
            <select
              value={selectedStatus}
              onChange={(e) => setSelectedStatus(e.target.value)}
              className="px-4 py-2 border rounded-lg bg-white hover:bg-gray-50 transition-colors duration-200"
            >
              <option value="all">All Statuses</option>
              {Object.entries(STATUS_CONFIG).map(([key, { label }]) => (
                <option key={key} value={key}>{label}</option>
              ))}
            </select>
            <button
              onClick={() => {
                setSelectedClient(null);
                setShowNewClientModal(true);
              }}
              className="bg-blue-600 text-white px-4 py-2 rounded-lg hover:bg-blue-700 flex items-center transition-colors duration-200"
            >
              <UserPlus size={20} className="mr-2" />
              Add Client
            </button>
          </div>
        </div>

        {clients.length === 0 ? (
          <div className="flex flex-col items-center justify-center h-64 bg-white rounded-lg shadow-sm p-8">
            <Users size={48} className="text-gray-300 mb-4" />
            <h3 className="text-xl font-medium text-gray-900 mb-2">Welcome to SalesSynth</h3>
            <p className="text-gray-500 mb-4 text-center">
              Track your fintech sales pipeline and client relationships in one place.
            </p>
            <button 
              onClick={() => setShowNewClientModal(true)} 
              className="bg-blue-600 text-white px-6 py-2 rounded-lg hover:bg-blue-700 transition-colors duration-200"
            >
              Add Your First Client
            </button>
          </div>
        ) : (
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
            {filteredClients.map(client => (
              <ClientCard 
                key={client.id} 
                client={client} 
                onEdit={handleEditClient}
                onToggleBookmark={handleToggleBookmark}
              />
            ))}
          </div>
        )}
      </div>

      <NewClientModal
        isOpen={showNewClientModal}
        onClose={() => {
          setShowNewClientModal(false);
          setSelectedClient(null);
        }}
        onSave={handleSaveClient}
        client={selectedClient}
      />
    </div>
  );
};

export default SalesSynth;
