import React, { useState, useEffect } from 'react';
import { X, Plus, Calendar, CheckCircle } from 'lucide-react';

const TaskPanel = ({ isOpen, onToggle }) => {
  const [tasks, setTasks] = useState(() => {
    const savedTasks = localStorage.getItem('tasks');
    return savedTasks ? JSON.parse(savedTasks) : [];
  });
  const [newTask, setNewTask] = useState('');
  const [dueDate, setDueDate] = useState('');
  const [showCompleted, setShowCompleted] = useState(false);

  useEffect(() => {
    localStorage.setItem('tasks', JSON.stringify(tasks));
  }, [tasks]);

  const handleAddTask = (e) => {
    e.preventDefault();
    if (!newTask.trim()) return;

    const task = {
      id: Date.now().toString(),
      title: newTask.trim(),
      dueDate: dueDate || null,
      completed: false,
      createdAt: new Date().toISOString()
    };

    setTasks(prev => [...prev, task]);
    setNewTask('');
    setDueDate('');
  };

  const toggleTaskCompletion = (taskId) => {
    setTasks(prev => prev.map(task => 
      task.id === taskId ? { ...task, completed: !task.completed } : task
    ));
  };

  const deleteTask = (taskId) => {
    setTasks(prev => prev.filter(task => task.id !== taskId));
  };

  const filteredTasks = tasks.filter(task => showCompleted ? true : !task.completed);

  return (
    <div className={`side-panel side-panel-right ${isOpen ? 'open' : ''}`}>
      <div className="panel-header">
        <h2 className="text-lg font-semibold">Tasks</h2>
        <button onClick={onToggle} className="text-gray-500 hover:text-gray-700">
          <X size={20} />
        </button>
      </div>

      <div className="panel-content">
        {/* Add Task Form */}
        <form onSubmit={handleAddTask} className="add-task-form">
          <input
            type="text"
            placeholder="Add a new task..."
            value={newTask}
            onChange={(e) => setNewTask(e.target.value)}
            className="add-task-input"
          />
          <div className="flex items-center space-x-2">
            <input
              type="date"
              value={dueDate}
              onChange={(e) => setDueDate(e.target.value)}
              className="add-task-input"
            />
            <button
              type="submit"
              className="btn-primary flex items-center"
              disabled={!newTask.trim()}
            >
              <Plus size={20} className="mr-1" />
              Add
            </button>
          </div>
        </form>

        {/* Task List */}
        <div className="mb-4 flex justify-between items-center">
          <h3 className="text-sm font-medium text-gray-700">
            {showCompleted ? 'All Tasks' : 'Active Tasks'}
          </h3>
          <button
            onClick={() => setShowCompleted(!showCompleted)}
            className="text-sm text-blue-600 hover:text-blue-700"
          >
            {showCompleted ? 'Hide Completed' : 'Show Completed'}
          </button>
        </div>

        <div className="space-y-2">
          {filteredTasks.length === 0 ? (
            <div className="panel-empty-state">
              <p className="text-gray-500">No tasks yet</p>
            </div>
          ) : (
            filteredTasks.map(task => (
              <div 
                key={task.id} 
                className="task-item"
              >
                <input
                  type="checkbox"
                  checked={task.completed}
                  onChange={() => toggleTaskCompletion(task.id)}
                  className="task-checkbox"
                />
                <div className="task-content">
                  <p className={`task-title ${task.completed ? 'line-through text-gray-400' : ''}`}>
                    {task.title}
                  </p>
                  {task.dueDate && (
                    <div className="task-due-date flex items-center">
                      <Calendar size={12} className="mr-1" />
                      {new Date(task.dueDate).toLocaleDateString()}
                    </div>
                  )}
                </div>
                <button
                  onClick={() => deleteTask(task.id)}
                  className="text-gray-400 hover:text-red-500"
                >
                  <X size={16} />
                </button>
              </div>
            ))
          )}
        </div>
      </div>
    </div>
  );
};

export default TaskPanel;
